query GetEveryEntityById($userId: ID, $profileId: ID, $postId: ID, $memberTypeId: ID) {
    user(id: $userId) {
        id
    },
    profile(id: $profileId) {
        id
    },
    post(id: $postId) {
        id
    },
    memberType(id: $memberTypeId) {
        id
    }
}

{
    users {
        id,
        firstName,
        profile {
            id
        },
        posts {
            id
        },
        memberType {
            id
        }
    }
}

query GetUserWithDependenciesById($userId: ID) {
    user(id: $userId) {
        id,
        profile {
            id
        },
        posts {
            id
        },
        memberType {
            id
        }
    }
}

{
