query GetUserWithSubscribedToUserById($userId: ID) {
    user(id: $userId) {
        id,
        subscribedToUser {
            id
        },
        posts {
            id,
            title
        }
    }
}

{
    users {
        id,
        firstName,
        userSubscribedTo {
            id,
            firstName,
            userSubscribedTo {
                id,
                firstName
            },
            subscribedToUser {
                id
            }
        }
        subscribedToUser {
            id,
            userSubscribedTo {
                id
            },
            subscribedToUser {
                id
            }
        }
    }
}

mutation CreateUser($firstName: String!, $lastName: String!, $email: String!) {
    createUser (data: {firstName: $firstName, lastName: $lastName, email: $email}) {
        id,
        firstName,
        lastName,
        email
    }
}

mutation CreateProfile($avatar: String!, $sex: String!, $birthday: Int!, $country: String!, $street: String!, $city: String!, $memberTypeId: ID!, $userId: ID!) {
    createProfile (data: {avatar: $avatar, sex: $sex, birthday: $birthday, country: $country, street: $street, city: $city, memberTypeId: $memberTypeId, userId: $userId}) {
        id
    }
}

mutation CreatePost($title: String!, $content: String!, $userId: ID!) {
    createPost (variables: {title: $title, content: $content, userId: $userId}) {
        id
    }
}

mutation UpdateUser($userId: ID!, $firstName: String!) {
    updateUser (userId: $userId, variables: {firstName: $firstName}) {
        id,
        firstName,
        lastName,
        email
    }
}

mutation CreateProfile($avatar: String!, $sex: String!, $birthday: Int!, $country: String!, $street: String!, $city: String!, $memberTypeId: ID!, $userId: ID!) {
    createProfile (variables: {avatar: $avatar, sex: $sex, birthday: $birthday, country: $country, street: $street, city: $city, memberTypeId: $memberTypeId, userId: $userId}) {
        id
    }
}

mutation CreatePost($title: String!, $content: String!, $userId: ID!) {
    createPost (variables: {title: $title, content: $content, userId: $userId}) {
        id
    }
}

mutation UpdateMemberType($id: ID!, $discount: Int, $monthPostsLimit: Int) {
    updateMemberType(id: $id, data: {discount: $discount, monthPostsLimit: $monthPostsLimit}) {
        id,
        discount,
        monthPostsLimit
    }
}

mutation SubscribeTo() {
    subscribeTo (variables: {title: $title, content: $content, userId: $userId}) {
        id
    }
}

mutation UnsubscribeFromUser($id: ID!, $userId: ID!) {
    unsubscribeFromUser (id: $id, userId: $userId) {
        id
    }
}

query GetUserWithDepthLimitById($userId: ID) {
    user(id: $userId) {
        id,
        userSubscribedTo {
            id,
            userSubscribedTo {
                id,
                userSubscribedTo {
                    id,
                    userSubscribedTo {
                        id,
                        userSubscribedTo {
                            id,
                            userSubscribedTo {
                                id
                            }
                        }
                    }
                }
            }
        }
    }
}
